import React, { useState } from "react";
import Card from './Card'
import {FiChevronLeft,FiChevronRight} from "react-icons/fi"

const Testimonial = (props) => {
    let reviews = props.reviews;
    const [index,setIndex] = useState(0); 

    function leftShiftHandler () {
        if(index - 1 < 0)
        {
            setIndex(reviews.lenght - 1);
        }
        else
        {
            setIndex(index - 1);
        }
    }

    function rightShiftHandler() {
        if(index + 1 >= reviews.lenght)
        {
            setIndex(0);
        }
        else
        {
            setIndex(index + 1);
        }
    }

    function surpriseHandler() {
       let randomIndex = Math.floor(Math.random() * reviews.lenght);
       setIndex(randomIndex);
    }
    return (
        <div>
        <Card review = {reviews[index]}></Card>
        <div className="flex text-3xl mt-5 gap-3 text-violet-400 font-bold justify-center items-center ">
                <button onClick={leftShiftHandler}
                className="cursor-pointer hover:text-violet-500">
                  <FiChevronLeft/>
                </button>
                <button onClick={rightShiftHandler}
                className="cursor-pointer hover:text-violet-500">
                  <FiChevronRight/>
                </button>
            </div>

            <div>
                <button onClick={surpriseHandler}
                className="bg-violet-400 hover:bg-violet-500 transition-all dration-200
                cursor-pointer px-10 py-2 rounded-md font-bold text-white text-lg">
                    Surprise Me
                </button>
            </div>
        </div>
    )
}

export default Testimonial
